import React, { Component } from 'react' ;

// component 객체를 생성
class App extends Component 
{
  // 하나의 component가 가질 수 있는 변경 가능한 데이터 
  state = {
    hello: 'hello app js!'
  } ;

  handleChange = () => {
    this.setState({
      hello: 'bye app js!'
    })
  }

  // Component class에 있던 render() method를 사용
  render()
  {
    // 변수를 넣을 때에는 중괄호를 사용해야 함
    return <div className="App">{this.state.hello}</div> ;
  }
}

// 만들어진 객체를 반환한다. 
export default App ;
